import React from 'react';
import {Switch , Route} from 'react-router';

import Products from 'containers/products'

const routes = (
    <Switch>
        <Route path='/' component = {Products} exact/>
        {/*<Route path='/basket' component = {Basket}/>*/}
    </Switch>
);



const Layout = () => (
    <div>{routes}</div>
);

export default Layout